import colors from "@/system/colors";

const spring = {
  background: {
    light: colors.red10,
    dark: colors.teal50,
  },
  text: {
    primary: {
      light: colors.red90,
      dark: colors.white,
    },
    secondary: {
      light: colors.red90,
      dark: colors.white,
    },
    accent: {
      light: colors.red50,
      dark: colors.yellow10,
    },
  },
};

const summer = {
  background: {
    light: colors.yellow10,
    dark: colors.green90,
  },
  text: {
    primary: {
      light: colors.blue90,
      dark: colors.white,
    },
    secondary: {
      light: colors.blue90,
      dark: colors.white,
    },
    accent: {
      light: colors.red50,
      dark: colors.white,
    },
  },
};
const autumn = {
  background: {
    light: colors.yellow10,
    dark: colors.red90,
  },
  text: {
    primary: {
      light: colors.red90,
      dark: colors.white,
    },
    secondary: {
      light: colors.red90,
      dark: colors.white,
    },
    accent: {
      light: colors.red50,
      dark: colors.yellow10,
    },
  },
};

const winter = {
  background: {
    light: colors.blue10,
    dark: colors.blue50,
  },
  text: {
    primary: {
      light: colors.blue90,
      dark: colors.white,
    },
    secondary: {
      light: colors.blue90,
      dark: colors.white,
    },
    accent: {
      light: colors.red50,
      dark: colors.yellow10,
    },
  },
};

export default { spring, summer, autumn, winter };
